import React,{useState} from "react";
import Card from "../../UI/Card/Card";
import Button from "../../UI/Button/Button";
import styles from './AddUser.module.css'
const AddUser = (props) => {
  const [enterUsername, setenterUsername] = useState('')
  const [enterAge, setenterAge] = useState(0)
  const addUserHandler = (event) => {
    event.preventDefault();
    console.log(enterUsername,enterAge)
    setenterUsername('');
    setenterAge(0);
  };

  const addNameHandler = (events) =>{
    setenterUsername(events.target.value);

  }

  const addAgeHandler = (events) =>{
    setenterAge(events.target.value);
    // console.log(events.target.value);
  }

  return (
    <Card className={styles.input}>
      <form onSubmit={addUserHandler}>
        <label htmlFor="username">Username</label>
        <input type="text" id="username" value={setenterUsername} onChange={addNameHandler} />
        <label htmlFor="Age">Age (in years)</label>
        <input type="number" id="Age" value={setenterAge} onChange={addAgeHandler} />
        <Button type="submit">Add User</Button>
      </form>
    </Card>
  );
};

export default AddUser;
